import React, { Component } from 'react';
import Table from './Table';
import Form from './Form';
import axios from 'axios'

class App extends Component {
   constructor(props) {
      super(props);
      this.state = {
        characters: [],
      };  
    }

   makePostCall(character){
      return axios.post('http://localhost:5000/products', character)
       .then(function (response) {
         console.log(response);
         if (response.status === 201) {
            return response;
         }
       })
       .catch(function (error) {
         console.log(error);
         return false;
       });
   }

   /*makeDeleteCall(id){
      console.log('http://localhost:5000/products/' + id)
      return axios.delete('http://localhost:5000/products/' + id)
       .then(function (response) {
         console.log(response);
         return (response.status === 200);
       })
       .catch(function (error) {
         console.log(error);
         return false;
       });
   }*/

   handleSubmit = character => {
      this.makePostCall(character).then( callResult => {
          if (callResult !== false) {
            this.setState({ characters: [...this.state.characters, callResult] });
          }
      });
    }

   removeCharacter = id => {
      const { characters } = this.state
    
      return axios.delete(`http://localhost:5000/products/${id}`)
        .then(response => {
          if (response.status === 204){
            this.setState({
              characters: characters.filter((character) => {
                return character.id !== id
              }),
            })
          }
      })
    }

   componentDidMount() {
      axios.get('http://localhost:5000/products')
       .then(res => {
         const characters = res.data.products_list;
         this.setState({ characters });
       })
       .catch(function (error) {
         //Not handling the error. Just logging into the console.
         console.log(error);
       });
   }

   render () {
      const { characters } = this.state;

      return (
         <div className = "container">
            <Table characterData={characters} removeCharacter={this.removeCharacter} />
            <Form handleSubmit={this.handleSubmit} />
         </div>
      );
   }
}

export default App
