import React, { Component } from 'react';
import Table from './Table';
import Form from './Form';
import FetchData from './FetchDemo';
import axios from 'axios'

class App extends Component {
   state = {
      characters: []
   };

   makePostCall(character){
      return axios.post('http://localhost:5000/users', character)
       .then(function (response) {
         console.log(response);
         return (response.status === 201);
       })
       .catch(function (error) {
         console.log(error);
         return false;
       });
   }

   makeDeleteCall(character, id){
      return axios.delete('http://localhost:5000/users/${id}', character)
       .then(function (response) {
         console.log(response);
         return (response.status === 200);
       })
       .catch(function (error) {
         console.log(error);
         return false;
       });
   }

   handleSubmit = character => {
      this.makePostCall(character).then( callResult => {
         if (callResult === true) {
            this.setState({ characters: [...this.state.characters, character] });
         }
      });
   }

   removeCharacter = index => {
      const { characters } = this.state

      const character = characters.find((c, i) => {
         return i === index
      })

      this.makeDeleteCall(character, character.id).then( callResult => {
         if (callResult == true) {
            this.setState({
               characters: characters.filter((c, i) => {
                  return i !== index
               }),
            })
         }
      });
   }

   componentDidMount() {
      axios.get('http://localhost:5000/users')
       .then(res => {
         const characters = res.data.users_list;
         this.setState({ characters });
       })
       .catch(function (error) {
         //Not handling the error. Just logging into the console.
         console.log(error);
       });
   }

   render () {
      const { characters } = this.state;

      return (
         <div className="container">
            <Table characterData={characters} removeCharacter={this.removeCharacter} />
            <Form handleSubmit={this.handleSubmit} />
            <FetchData subreddit='calpoly' />
         </div>
      );
   }
}

export default App
