  class InventoryManagementApp extends React.Component{
    constructor(){
      super();
      this.state = {
        activeTab: 1,
        inventory: {
          categories:{
            dresses:[],
            shirts:[
  {category: "shirts", name: "Blue T-Shirt", price: "16.99", imageURL: "https://cdn.shopify.com/s/files/1/0797/0169/products/mockup-c6d64730_1024x1024.jpg"}],
